function openMenu_ColorMC() {
    document.getElementById("cc_ColorMC").style.display = "initial";
    document.getElementById("close_ColorMC").style.display = "initial";
    document.getElementById("open_ColorMC").style.display = "none";
}

function closeMenu_ColorMC() {
    document.getElementById("cc_ColorMC").style.display = "none";
    document.getElementById("close_ColorMC").style.display = "none";
    document.getElementById("open_ColorMC").style.display = "initial";
}